SELECT RESVE_NO, RSVCTM, TO_CHAR(RSVDE) AS RSVDE, RSVTM, VISITR_CO, RESVE_REQ, RESVE_PHONE, R_NO, RESVE_AMOUNT
FROM TB_RESVE
WHERE R_NO = #{ rNo }

